/**
 * A fragment representing a list of Items.
 */
public class RestaurateFragment extends Fragment {
    RecyclerView recyclerView; //TODO: recomendacion para tener al alcance las variables de trabajo.
    MyRestaurateRecyclerViewAdapter adapterRestaurantes;
    List<Restaurante> restauranteList;

    // TODO: Customize parameter argument names
    private static final String ARG_COLUMN_COUNT = "column-count";
    // TODO: Customize parameters
    private int mColumnCount = 1;

    /*private OnFragmentInteractionListener mListener;
    public interface OnFragmentInteractionListener{
        void onFragmentInteraction(Restaurante restaurante);
    }*/


    public RestaurateFragment() {
    }

    // TODO: Customize parameter initialization
    @SuppressWarnings("unused")
    public static RestaurateFragment newInstance(int columnCount) {
        RestaurateFragment fragment = new RestaurateFragment();
        Bundle args = new Bundle();
        args.putInt(ARG_COLUMN_COUNT, columnCount);
        fragment.setArguments(args);
        return fragment;
    }

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        if (getArguments() != null) {
            mColumnCount = getArguments().getInt(ARG_COLUMN_COUNT);
        }
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_item_list, container, false);

        // Set the adapter
        if (view instanceof RecyclerView) { //TODO:Siempre será true debido
            Context context = view.getContext();
            /*RecyclerView*/recyclerView = (RecyclerView) view;
            if (mColumnCount <= 1) {
                recyclerView.setLayoutManager(new LinearLayoutManager(context));
            } else {
                recyclerView.setLayoutManager(new GridLayoutManager(context, mColumnCount));
            }
            //Asociamos el adapador al cyclerview

            //Lista de elementos (Restaurantes)
            String URLLogoTacosPipe = "data:image/jpeg;base64,";
            String URLLogoDominos = "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3e/Domino%27s_pizza_logo.svg/1200px-Domino%27s_pizza_logo.svg.png";
            restauranteList = new ArrayList<>();
            for(int i=0;i<36000;i++){
                restauranteList.add(new Restaurante("Domninos pizza",URLLogoDominos, 3.0f,"Madrid españa"));
                restauranteList.add(new Restaurante("Tacos pipe",URLLogoTacosPipe, 4.0f,"Madrid"));
            }
            adapterRestaurantes = new MyRestaurateRecyclerViewAdapter(getActivity(),restauranteList); //Se agregó contexto como parametro
            //para utilizar librería glide
            recyclerView.setAdapter(adapterRestaurantes);
        }
        return view;
    }

   /* @Override
    public void onAttach(@NonNull Context context) {
        super.onAttach(context);
    }*/
}